import UIKit

class PokedexRouter: PokedexWireframeProtocol {
    
    weak var viewController: UIViewController?
    
    static func createModule(type: PokemonType? = nil) -> UIViewController {
        // Change to get view from storyboard if not using progammatic UI
        let view = PokedexViewController(nibName: nil, bundle: nil)
        let interactor = PokedexInteractor()
        let router = PokedexRouter()
        let presenter = PokedexPresenter(interface: view, interactor: interactor, router: router)
        
        view.presenter = presenter
        interactor.presenter = presenter
        router.viewController = view
        view.pokemonType = type
        
        return view
    }
    
    func goToPokemonDetail(id: Int) {
        let pokemonDetail = PokemonDetailRouter.createModule(id: id)
        viewController?.navigationController?.pushViewController(pokemonDetail, animated: true)
    }
}
